INSERT INTO `tiendos` VALUES (2,99,NULL,NULL,'','<p><strong>1- Kết cấu phần th&acirc;n:&nbsp;</strong></p>\r\n\r\n<p>&nbsp;Ho&agrave;n th&agrave;nh s&agrave;n tầng 25, đang thi c&ocirc;ng cột v&aacute;ch tầng 25</p>\r\n\r\n<p><strong>2 - X&acirc;y th&ocirc;:</strong></p>\r\n\r\n<p>Ho&agrave;n th&agrave;nh x&acirc;y tầng 17 đang thi x&acirc;y tầng 18,19</p>\r\n\r\n<p><strong>3 - Tr&aacute;t ho&agrave;n thiện:</strong></p>\r\n\r\n<p>&nbsp;Ho&agrave;n th&agrave;nh thi c&ocirc;ng tr&aacute;t ngo&agrave;i từ s&agrave;n tầng 15 xuống tầng 3, đang thi c&ocirc;ng tr&aacute;t tầng 18 xuống tầng 15</p>\r\n\r\n<p>Một số h&igrave;nh ảnh thi c&ocirc;ng của dự &aacute;n</p>\r\n\r\n<p><img alt=\"tiến độ sunshine palace\" src=\"http://sunshinegroup.vn/Portals/1//tiendo/2016/10/P-20161014-1.jpg\" style=\"height:480px; width:640px\" /></p>\r\n\r\n<p><img alt=\"tiến độ sunshine palace 14-10\" src=\"http://sunshinegroup.vn/Portals/1//tiendo/2016/10/P-20161014-3.jpg\" style=\"height:480px; width:640px\" /></p>\r\n\r\n<p><img alt=\"tiến độ sunshine palace 14-10-1\" src=\"http://sunshinegroup.vn/Portals/1//tiendo/2016/10/P-20161014-2.jpg\" style=\"height:480px; width:640px\" /></p>\r\n\r\n<p><img alt=\"tiến độ sunshine palace 14-10-2\" src=\"http://sunshinegroup.vn/Portals/1//tiendo/2016/10/P-20161014-4.jpg\" style=\"height:480px; width:640px\" /></p>\r\n',NULL,NULL,NULL,1),(3,100,NULL,NULL,NULL,'<p>1- Kết cấu phần th&acirc;n:&nbsp;</p>\r\n\r\n<p>&nbsp; + Khối Chung cư: Ho&agrave;n th&agrave;nh 50% s&agrave;n tầng 26, đang thi c&ocirc;ng dầm s&agrave;n tầng 26 ph&acirc;n đoạn 2</p>\r\n\r\n<p>&nbsp; + Khối Duplex: đang thi c&ocirc;ng cop pha dầm s&agrave;n tầng &aacute;p m&aacute;i</p>\r\n\r\n<p>2 - X&acirc;y th&ocirc;:</p>\r\n\r\n<p>&nbsp; + Khối Chung cư: Ho&agrave;n th&agrave;nh x&acirc;y tầng 18 đang thi x&acirc;y tầng 19</p>\r\n\r\n<p>&nbsp; + Khối Duplex: đang thi c&ocirc;ng x&acirc;y tầng 3,4</p>\r\n\r\n<p>3 - Tr&aacute;t ho&agrave;n thiện:</p>\r\n\r\n<p>&nbsp; + Khối Chung cư: ho&agrave;n th&agrave;nh thi c&ocirc;ng tr&aacute;t ngo&agrave;i từ s&agrave;n tầng 15 xuống tầng 3, đang thi c&ocirc;ng tr&aacute;t tầng 18 xuống tầng 15</p>\r\n\r\n<p><strong>Một số h&igrave;nh ảnh thi c&ocirc;ng của dự &aacute;n</strong></p>\r\n\r\n<p><img alt=\"Cập nhật tiến độ ngày 23 tháng 10 năm 2016\" src=\"http://sunshinegroup.vn/Portals/1//tiendo/2016/10/P-20161023-1.jpg\" style=\"height:480px; width:640px\" /></p>\r\n\r\n<p><img alt=\"cập nhập tiến độ thi công 23-10\" src=\"http://sunshinegroup.vn/Portals/1//tiendo/2016/10/P-20161023-2.jpg\" style=\"height:480px; width:640px\" /></p>\r\n\r\n<p><img alt=\"cập nhập tiến độ\" src=\"http://sunshinegroup.vn/Portals/1//tiendo/2016/10/P-20161023-3.jpg\" style=\"height:480px; width:640px\" /></p>\r\n\r\n<p><img alt=\"cập nhập tiến độ\" src=\"http://sunshinegroup.vn/Portals/1//tiendo/2016/10/P-20161023-4.jpg\" style=\"height:480px; width:640px\" /></p>\r\n\r\n<p><img alt=\"cập nhập tiến độ 23-10-2\" src=\"http://sunshinegroup.vn/Portals/1//tiendo/2016/10/P-20161023-5.jpg\" style=\"height:480px; width:640px\" /></p>\r\n\r\n<p><img alt=\"cập nhật tiến độ ngày 23-10-3\" src=\"http://sunshinegroup.vn/Portals/1//tiendo/2016/10/P-20161023-6.jpg\" style=\"height:480px; width:640px\" /></p>\r\n',NULL,NULL,NULL,1),(6,106,NULL,NULL,NULL,'<p><strong>1- Kết cấu phần th&acirc;n:</strong>&nbsp;</p>\r\n\r\n<p>&nbsp; + Khối Chung cư: Ho&agrave;n th&agrave;nh 50% s&agrave;n tầng 24, đang thi c&ocirc;ng dầm s&agrave;n tầng 24 ph&acirc;n đoạn 2</p>\r\n\r\n<p>&nbsp; + Khối Duplex: ho&agrave;n th&agrave;nh s&agrave;n tầng 16, đang thi c&ocirc;ng cột v&aacute;ch tầng 16&nbsp;</p>\r\n\r\n<p><strong>2 - X&acirc;y th&ocirc;:</strong></p>\r\n\r\n<p>&nbsp; + Ho&agrave;n th&agrave;nh x&acirc;y khu thương mại tầng 1, tầng 2</p>\r\n\r\n<p>&nbsp; + Khối Chung cư: Ho&agrave;n th&agrave;nh x&acirc;y tầng 16,17 đang thi x&acirc;y tầng 18</p>\r\n\r\n<p>&nbsp; + Khối Duplex: đang thi c&ocirc;ng x&acirc;y tầng 3</p>\r\n\r\n<p><strong>3 - Tr&aacute;t ho&agrave;n thiện:</strong></p>\r\n\r\n<p>&nbsp; + Ho&agrave;n th&agrave;nh tr&aacute;t trong, ngo&agrave;i khu thương mại tầng 1, tầng 2</p>\r\n\r\n<p>&nbsp; + Khối Chung cư: ho&agrave;n th&agrave;nh thi c&ocirc;ng tr&aacute;t ngo&agrave;i từ s&agrave;n tầng 12 xuống tầng 3, đang thi c&ocirc;ng tr&aacute;t tầng 15 xuống tầng 13</p>\r\n\r\n<p><strong>Một số h&igrave;nh ảnh thi c&ocirc;ng của dự &aacute;n</strong></p>\r\n\r\n<p><strong><img alt=\"cập nhật tiến độ ngày 30 tháng 9\" src=\"http://sunshinegroup.vn/Portals/1//tiendo/2016/9/P-20160930-2.jpg\" style=\"height:480px; width:640px\" /></strong></p>\r\n\r\n<p><img alt=\"tiến độ sunshine palace\" src=\"http://sunshinegroup.vn/Portals/1//tiendo/2016/9/P-20160930-6.jpg\" style=\"height:480px; width:640px\" /></p>\r\n\r\n<p><img alt=\"tiến độ sunshine palace 2\" src=\"http://sunshinegroup.vn/Portals/1//tiendo/2016/9/P-20160930-7.jpg\" style=\"height:480px; width:640px\" /></p>\r\n\r\n<p><img alt=\"tiến độ sunshine palace 3\" src=\"http://sunshinegroup.vn/Portals/1//tiendo/2016/9/P-20160930-5.jpg\" style=\"height:480px; width:640px\" /></p>\r\n',NULL,NULL,NULL,1),(7,107,NULL,NULL,NULL,'<p>Tiến độ thi c&ocirc;ng thực tại của dự &aacute;n Sunshine Palace</p>\r\n\r\n<p>1- Kết cấu phần th&acirc;n:&nbsp;</p>\r\n\r\n<p>&nbsp; + Khối Chung cư: Ho&agrave;n th&agrave;nh s&agrave;n tầng 15, đang thi c&ocirc;ng dầm s&agrave;n tầng 16</p>\r\n\r\n<p>&nbsp; + Khối Duplex: ho&agrave;n th&agrave;nh s&agrave;n tầng 11, đang thi c&ocirc;ng dầm s&agrave;n tầng 12&nbsp;</p>\r\n\r\n<p>2 - X&acirc;y th&ocirc;:</p>\r\n\r\n<p>&nbsp; + Ho&agrave;n th&agrave;nh x&acirc;y khu thương mại tầng 1, tầng 2</p>\r\n\r\n<p>&nbsp; + Khối Chung cư: Ho&agrave;n th&agrave;nh x&acirc;y tầng 3,4,5,6,7, 8 đang thi x&acirc;y tầng 9,10</p>\r\n\r\n<p>&nbsp; + Khối Duplex: ho&agrave;n th&agrave;nh x&acirc;y tầng 3, đang thi c&ocirc;ng tầng 4,5,6&nbsp;</p>\r\n\r\n<p>3 - Tr&aacute;t ho&agrave;n thiện:</p>\r\n\r\n<p>&nbsp; + Đang ho&agrave;n thiện tr&aacute;t trong, ngo&agrave;i khu thương mại tầng 1, tầng 2</p>\r\n\r\n<p>&nbsp; + Khối Chung cư: Bắt đầu thi c&ocirc;ng tr&aacute;t ngo&agrave;i từ s&agrave;n tầng 9 xuống</p>\r\n\r\n<p>&nbsp; + Khối Duplex:&nbsp;Bắt đầu thi c&ocirc;ng tr&aacute;t ngo&agrave;i từ s&agrave;n tầng 7 xuống</p>\r\n\r\n<p><img alt=\"\" src=\"http://sunshinegroup.vn/Portals/1//IMG_6128.jpg\" style=\"height:853px; width:640px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"http://sunshinegroup.vn/Portals/1//IMG_6127.jpg\" style=\"height:480px; width:640px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"http://sunshinegroup.vn/Portals/1//IMG_6105.jpg\" style=\"height:480px; width:640px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"http://sunshinegroup.vn/Portals/1//IMG_6117.jpg\" style=\"height:480px; width:640px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"http://sunshinegroup.vn/Portals/1//IMG_6086.jpg\" style=\"height:480px; width:640px\" /></p>\r\n',NULL,NULL,NULL,10),(8,109,NULL,NULL,NULL,'',NULL,NULL,NULL,1),(9,112,NULL,NULL,NULL,'<p>1. Kết cầu phần th&acirc;n:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Cất n&oacute;c dự &aacute;n v&agrave; đang tiến h&agrave;nh thi c&ocirc;ng s&agrave;n&nbsp;tầng &aacute;p m&aacute;i&nbsp; &nbsp; &nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p>\r\n\r\n<p>&nbsp;2. Thi c&ocirc;ng phần x&acirc;y th&ocirc;:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Ho&agrave;n th&agrave;nh 80% thi c&ocirc;ng x&acirc;y tầng 20, tầng 21&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p>\r\n\r\n<p>3. Tr&aacute;t ho&agrave;n thiện:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp;Ho&agrave;n th&agrave;nh 70% thi c&ocirc;ng tr&aacute;t ngo&agrave;i tầng 20 đến tầng 15&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p><strong>Một số h&igrave;nh ảnh thi c&ocirc;ng dự &aacute;n</strong></p>\r\n\r\n<p><img alt=\"\" src=\"/app/webroot/uploads/files/Palace_15_11.png\" style=\"height:476px; width:640px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/app/webroot/uploads/files/Palace_15_11_1.png\" style=\"height:476px; width:640px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/app/webroot/uploads/files/Palace_15_11_2.png\" style=\"height:476px; width:640px\" /></p>\r\n',NULL,NULL,NULL,1),(10,113,NULL,NULL,NULL,'<p><span style=\"font-family:verdana,sans-serif; font-size:10.0pt\">&nbsp;1- Kết cấu phần th&acirc;n:&nbsp;</span></p>\r\n\r\n<p><span style=\"font-family:verdana,sans-serif; font-size:10.0pt\">&nbsp; Dự &aacute;n đ&atilde; được cất n&oacute;c v&agrave; đang tiếp tục ho&agrave;n thiền&nbsp;s&agrave;n &aacute;p m&aacute;i đặt 50% khối lượng c&ocirc;ng việc</span></p>\r\n\r\n<p><span style=\"font-family:verdana,sans-serif; font-size:10.0pt\">2 - X&acirc;y th&ocirc;:</span></p>\r\n\r\n<p><span style=\"font-family:verdana,sans-serif; font-size:10.0pt\">Đ&atilde; ho&agrave;n th&agrave;nh x&acirc;y xong tầng 20 đang gấp r&uacute;t triển khai x&acirc;y tầng 21 đạt 70%</span></p>\r\n\r\n<p><span style=\"font-family:verdana,sans-serif; font-size:10.0pt\">3 - Tr&aacute;t ho&agrave;n thiện:</span></p>\r\n\r\n<p><span style=\"font-family:verdana,sans-serif; font-size:10.0pt\">&nbsp;Ho&agrave;n th&agrave;nh thi c&ocirc;ng tr&aacute;t ngo&agrave;i từ s&agrave;n tầng 17 xuống tầng 3, đang thi c&ocirc;ng tr&aacute;t tầng 20 xuống tầng 18</span></p>\r\n\r\n<p><strong><span style=\"font-family:verdana,sans-serif; font-size:10.0pt\">Một số h&igrave;nh ảnh tiến độ thi c&ocirc;ng dự &aacute;n:</span></strong></p>\r\n\r\n<p><img alt=\"\" src=\"/app/webroot/uploads/files/P-20161118-2.jpg\" style=\"height:1160px; width:870px\" /><img alt=\"\" src=\"/app/webroot/uploads/files/P-20161118-1.jpg\" style=\"height:1160px; width:870px\" /><img alt=\"\" src=\"/app/webroot/uploads/files/P-20161118-3.jpg\" style=\"height:1160px; width:870px\" /><img alt=\"\" src=\"/app/webroot/uploads/files/P-20161118-4.jpg\" style=\"height:1160px; width:870px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/app/webroot/uploads/files/P-20161118-5.jpg\" /><img alt=\"\" src=\"/app/webroot/uploads/files/P-20161118-6.jpg\" /><img alt=\"\" src=\"/app/webroot/uploads/files/P-20161118-7.jpg\" /></p>\r\n',NULL,NULL,NULL,1),(11,130,NULL,NULL,NULL,'<p>K&iacute;nh gửi Qu&yacute; kh&aacute;ch h&agrave;ng!</p>\r\n\r\n<p>Tập đo&agrave;n Sunshine xin tr&acirc;n trọng th&ocirc;ng b&aacute;o c&aacute;c hạng mục thi c&ocirc;ng của dự &aacute;n Sunshine Palace th&aacute;ng 11 năm 2016 như sau:</p>\r\n\r\n<p>1- Kết cấu phần th&acirc;n:&nbsp;</p>\r\n\r\n<p>&nbsp;Ho&agrave;n thiện xong thi c&ocirc;ng dầm s&agrave;n tầng &aacute;p m&aacute;i</p>\r\n\r\n<p>&nbsp;2 - X&acirc;y th&ocirc;:</p>\r\n\r\n<p>Ho&agrave;n th&agrave;nh x&acirc;y tầng 20 đang thi x&acirc;y tầng 21 đạt 70%</p>\r\n\r\n<p>&nbsp;&nbsp;3 - Tr&aacute;t ho&agrave;n thiện:</p>\r\n\r\n<p>Ho&agrave;n th&agrave;nh thi c&ocirc;ng tr&aacute;t ngo&agrave;i tầng 20-21</p>\r\n\r\n<p>4 - Sơn:</p>\r\n\r\n<p>&nbsp; + Đang tiến h&agrave;nh sơn chống thấm, sơn l&oacute;t ngo&agrave;i nh&agrave; ở tầng 14-17</p>\r\n\r\n<p><strong>Dưới đ&acirc;y l&agrave; 1 số h&igrave;nh ảnh thi c&ocirc;ng của dự &aacute;n:</strong></p>\r\n\r\n<p><img alt=\"\" src=\"/app/webroot/uploads/files/P-20161126-1.jpg\" style=\"height:653px; width:867px\" /><img alt=\"\" src=\"/app/webroot/uploads/files/P-20161126-2.jpg\" /><img alt=\"\" src=\"/app/webroot/uploads/files/P-20161126-3.jpg\" /><img alt=\"\" src=\"/app/webroot/uploads/files/P-20161126-4.jpg\" /></p>\r\n',NULL,NULL,NULL,1),(12,133,NULL,NULL,NULL,'<p style=\"text-align:justify\"><strong><em>Với tinh thần l&agrave;m việc hăng h&aacute;i của chủ đầu tư cũng như c&aacute;c đơn vị nh&agrave; thầu, dự &aacute;n Sunshine Palace đang kiện to&agrave;n c&aacute;c hạng mục đ&uacute;ng như tiến độ đ&atilde; đặt ra từu trước, một &ldquo;si&ecirc;u phẩm&rdquo; mang phong c&aacute;ch Ch&acirc;u &Acirc;u &ndash; T&acirc;n cổ điển đang dần hiện ra r&otilde; rệt để đ&oacute;n những chủ nh&acirc;n đầu ti&ecirc;n.</em></strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>I/ Phần x&acirc;y th&ocirc;</strong></p>\r\n\r\n<p style=\"text-align:justify\">Về cơ bản đến nay đ&atilde; ho&agrave;n th&agrave;nh xong to&agrave;n bộ hạng mục thi c&ocirc;ng kết cấu phần th&acirc;n v&agrave; x&acirc;y th&ocirc;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>II/ Phần ho&agrave;n thiện</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>1/ Ngo&agrave;i nh&agrave;: </strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>+ </strong><em>Tr&aacute;t tường:Tr&aacute;t ngo&agrave;i trục 14-15, B-E</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>+ Sơn chống thấm v&agrave; sơn l&oacute;t: Đang thi c&ocirc;ng trục 9-15/E từ tầng 23 xuống tầng 3 đạt 37% khối lượng c&ocirc;ng việc</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>+ Cửa nh&ocirc;m k&iacute;nh: Đang ho&agrave;n thiện lắp dựng cửa nh&ocirc;m k&iacute;nh tầng 3,4,5</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>+ Lan can L&ocirc; gia: Đang lắp lan can l&ocirc; gia từ tầng 22 đến tầng 26</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>2/ Trong nh&agrave;</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>+ Tr&aacute;t trong Đang tr&aacute;t tường trong căn hộ tầng 20,21,21,23</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>+ Sơn bả: Thi c&ocirc;ng sơn bả lớp 1 đạt 26%</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>+ Ốp l&aacute;t: Hiện đang thi c&ocirc;ng l&aacute;t nền khu WC, sảnh bếp v&agrave; l&aacute;ng nền căn hộ</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>Đang ốp gạch 300x600mm cho c&aacute;c tầng 3,7,10,12,13</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>+ Hệ thống điện, điện nhẹ: Ho&agrave;n th&agrave;nh thi c&ocirc;ng điện &acirc;m tường tầng 24, đang thi c&ocirc;ng điện &acirc;m tường tầng 22,23,25</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>3/&nbsp; Ho&agrave;n th&agrave;nh thi c&ocirc;ng tầng hầm</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>+ Đ&atilde; ho&agrave;n th&agrave;nh về cơ bản lắp điều h&ograve;a th&ocirc;ng gi&oacute;</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>+ Hệ thống ph&ograve;ng ch&aacute;y chữa ch&aacute;y nhiều hạng mục chuẩn bị ho&agrave;n th&agrave;nh, bắt đầu lắp đặt ống th&eacute;p D100 tầng 2 khối chung cư</em></p>\r\n\r\n<p style=\"text-align:justify\"><strong>II/ Hạ tầng ngo&agrave;i nh&agrave;</strong></p>\r\n\r\n<p style=\"text-align:justify\"><em>Gấp r&uacute;t triển khai gấp tuyến đường nội bộ trước t&ograve;a nh&agrave;</em></p>\r\n\r\n<p style=\"text-align:justify\"><strong>III, Phần Căn hộ</strong></p>\r\n\r\n<p style=\"text-align:justify\"><em>Chuẩn bị ho&agrave;n th&agrave;nh phần x&acirc;y dựng v&agrave; ho&agrave;n th&agrave;nh thi c&ocirc;ng lắp đặt thiết bị nội thất b&ecirc;n trong căn hộ.</em></p>\r\n\r\n<p style=\"text-align:justify\"><strong>IV, Một số h&igrave;nh ảnh chụp thực tế tại c&ocirc;ng tr&igrave;nh Sunshine Palace cập nhật đến ng&agrave;y 9/3/2017</strong></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/app/webroot/uploads/files/thi-cong-trat-xay-dung(1).png\" style=\"height:340px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em><img alt=\"\" src=\"/app/webroot/uploads/files/Cong-nhan-dang-xay-tuong-ben-trong(1).png\" style=\"height:340px; width:600px\" /></em></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/app/webroot/uploads/files/thi-cong-hoan-thien(1).png\" style=\"height:340px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/app/webroot/uploads/files/op-tuong-da(1).png\" style=\"height:340px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/app/webroot/uploads/files/he-thong-cap-nuoc(1).png\" style=\"height:340px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/app/webroot/uploads/files/he-thong-dien-dang-lap-dat(2).png\" style=\"height:340px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/app/webroot/uploads/files/dang-thi-cong-hang-muc-phong-chay-chua-chay(1).png\" style=\"height:340px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em><img alt=\"\" src=\"/app/webroot/uploads/files/lan-can-lo-gia-lap-dat-kien-co(2).png\" style=\"height:340px; width:600px\" /></em></p>\r\n\r\n<p style=\"text-align:center\"><em><img alt=\"\" src=\"/app/webroot/uploads/files/thi-cong-ha-tang-cat-duoc-san-phang(1).png\" style=\"height:340px; width:600px\" /></em></p>\r\n',NULL,NULL,NULL,1),(13,136,NULL,NULL,NULL,'<p><strong><em>Đ&uacute;ng như những g&igrave; được thị trường v&agrave; kh&aacute;ch h&agrave;ng ghi nhận, Sunshine Palace đang từng bước trở th&agrave;nh một dự &aacute;n c&oacute; sự &ldquo;hấp dẫn v&agrave; cuốn h&uacute;t&rdquo; ri&ecirc;ng giữa khu vực Ho&agrave;ng Mai &ndash; nơi tập trung nhiều dự &aacute;n lớn hiện nay.</em></strong></p>\r\n\r\n<p><strong><em>Với c&ocirc;ng t&aacute;c triển khai thi c&ocirc;ng &ldquo;thần tốc&rdquo; của chủ đầu tư c&ugrave;ng c&aacute;c đơn vị nh&agrave; thầu, dự &aacute;n đ&atilde; v&agrave; đang ho&agrave;n thiện c&aacute;c hạng mục đ&uacute;ng tiến độ với chất lượng cao nhất.</em></strong></p>\r\n\r\n<p><strong><em>T&iacute;nh đến ng&agrave;y 22/02/2017, tiến độ thi c&ocirc;ng của dự &aacute;n Sunshine Palace được thực hiện như sau:</em></strong></p>\r\n\r\n<p><strong>I/ </strong><strong>Phần x&acirc;y th&ocirc;</strong></p>\r\n\r\n<p>Dự &aacute;n Sunshine Palace đ&atilde; được Chủ đầu tư tiến h&agrave;nh cất n&oacute;c v&agrave;o ng&agrave;y 15/11/2016 v&agrave; đến nay đ&atilde; ho&agrave;n th&agrave;nh xong to&agrave;n bộ hạng mục thi c&ocirc;ng kết cấu phần th&acirc;n, c&ocirc;ng t&aacute;c x&acirc;y th&ocirc; đạt đến 98% khối lượng c&ocirc;ng việc.</p>\r\n\r\n<p><strong>II/ Phần ho&agrave;n thiện</strong></p>\r\n\r\n<p><strong>1. Ngo&agrave;i nh&agrave;:</strong>C&ocirc;ng t&aacute;c tr&aacute;t tường, sơn, cửa k&iacute;nh, logia, lan can.. vẫn tiếp tục được triển khai v&agrave; dần đi v&agrave;o ho&agrave;n thiện</p>\r\n\r\n<p>- <em>Tr&aacute;t tường</em>:Đ&atilde; triển khai xong từ tầng 3 đến tầng 23 đạt đến 90% khối lượng c&ocirc;ng việc.</p>\r\n\r\n<p>- <em>Sơn chống thấm v&agrave; sơn l&oacute;t</em>: Đang tiếp tục triển khai từ tầng 3- tầng 23 đạt hơn 20% khối lượng.</p>\r\n\r\n<p>- <em>Cửa nh&ocirc;m k&iacute;nh</em>:Đ&atilde; ho&agrave;n thiện xong thi c&ocirc;ng lắp đặt cửa sổ c&aacute;c tầng 3,4,5</p>\r\n\r\n<p>- <em>Lan can L&ocirc; gia</em>: Đ&atilde; ho&agrave;n thanh lắp dựng lan can logia đến tầng 21 của dự &aacute;n.</p>\r\n\r\n<p><strong>2. Trong nh&agrave;</strong></p>\r\n\r\n<p>- <em>Tr&aacute;t trong</em>: Hiện đ&atilde; ho&agrave;n th&agrave;nh đến tầng 21 v&agrave; đang tiếp tục triển khai từ tầng 22 đến tầng 24 của dự &aacute;n.</p>\r\n\r\n<p>- <em>Sơn bả:</em>Hiện vẫn đang tiếp tục triển khai trong nh&agrave; từ tầng 3 đến tầng 8 v&agrave; đặt khoảng 10% khối lượng c&ocirc;ng việc.</p>\r\n\r\n<p>- <em>Ốp l&aacute;t:</em> C&ocirc;ng t&aacute;c n&agrave;y đang được thi c&ocirc;ng từ tầng 3 đến tầng 5 đạt khoảng 75% v&agrave; hiện vẫn đang tiếp tục cho triển khai từ tầng 6 đến tầng 9</p>\r\n\r\n<p>- <em>Hệ thống điện, điện nhẹ</em>: G&oacute;i n&agrave;y hiện đ&atilde; ho&agrave;n th&agrave;nh xong phần đi ống ngần, nền, trần, tường của căn hộ từ tầng 3 đến tầng 23 v&agrave; đang tiến h&agrave;nh lắp đặt ngăn tường tầng 25 đạt khoảng 20% tiến độ c&ocirc;ng việc.</p>\r\n\r\n<p><strong>3. Ho&agrave;n thiện thi c&ocirc;ng phần hầm</strong></p>\r\n\r\n<p>- Hiện đ&atilde; ho&agrave;n th&agrave;nh xong phần đi ống luồn d&acirc;y v&agrave; m&aacute;ng điện v&agrave; đang tiến h&agrave;nh thi c&ocirc;ng hệ thống cấp tho&aacute;t nước cho t&ograve;a nh&agrave;</p>\r\n\r\n<p>- Hệ thống điều h&ograve;a th&ocirc;ng gi&oacute; đ&atilde; ho&agrave;n th&agrave;nh xong</p>\r\n\r\n<p>- Hệ thống PCCC đ&atilde; ho&agrave;n th&agrave;nh xong phần đi ống</p>\r\n\r\n<p><strong>II/ Về hạ tầng ngo&agrave;i nh&agrave;</strong></p>\r\n\r\n<p>Chủ đầu tư Sunshine Group đang tiến h&agrave;nh cho triển khai gấp tuyến đường nội bộ mặt trước của t&ograve;a nh&agrave;, c&ocirc;ng t&aacute;c hiện tại đang chuẩn bị giải v&acirc;y thảm nhựa</p>\r\n\r\n<p><strong>III/ Về Phần căn hộ: </strong></p>\r\n\r\n<p>Hiện đ&atilde; ho&agrave;n th&agrave;nh đến 90%phần x&acirc;y dựng v&agrave; chuẩn tiến h&agrave;nh thi c&ocirc;ng lắp đặt thiết bị nội thất b&ecirc;n trong căn hộ. Dự kiến trong th&aacute;ng tới chủ đầu tư sẽ tiến h&agrave;nh lắp đặt thang m&aacute;y cho to&agrave;n bộ dự &aacute;n.</p>\r\n\r\n<p><strong>Dưới đ&acirc;y l&agrave; một số h&igrave;nh ảnh chụp thực tế tiến độ thi c&ocirc;ng tại Sunshine Palace:</strong></p>\r\n\r\n<p><img alt=\"\" src=\"/app/webroot/uploads/files/Tien-do/2202/2.jpg\" /><img alt=\"\" src=\"/app/webroot/uploads/files/Tien-do/2202/3.jpg\" /><img alt=\"\" src=\"/app/webroot/uploads/files/Tien-do/2202/4.jpg\" /><img alt=\"\" src=\"/app/webroot/uploads/files/Tien-do/2202/5.jpg\" /><img alt=\"\" src=\"/app/webroot/uploads/files/Tien-do/2202/6.jpg\" /><img alt=\"\" src=\"/app/webroot/uploads/files/Tien-do/2202/7.jpg\" /></p>\r\n',NULL,NULL,NULL,1),(14,137,NULL,NULL,NULL,'<p><strong>Chủ đầu tư Sunshine Group c&ugrave;ng c&aacute;c nh&agrave; thầu đang nỗ lực hết m&igrave;nh để mang đến những căn hộ Sunshine Palace chất lượng cao nhất v&agrave; đảm bảo theo đ&uacute;ng tiến độ đề ra</strong>.</p>\r\n\r\n<p>T&iacute;nh đến ng&agrave;y 17/12/2016, dự &aacute;n đ&atilde; ho&agrave;n th&agrave;nh xong to&agrave;n bộ kết cấu phần th&acirc;n, c&aacute;c hạng mục kh&aacute;c vẫn đang tiếp tục được đẩy mạnh thi c&ocirc;ng: x&acirc;y tường (đ&atilde; đến tầng 24);&nbsp; phần tr&aacute;t ngo&agrave;i (đến tầng 23) v&agrave; x&acirc;y căn hộ (đến tầng 10).</p>\r\n\r\n<p>Dưới đ&acirc;y l&agrave; những h&igrave;nh ảnh mới nhất tại c&ocirc;ng trường dự &aacute;n Sunshine Palace:</p>\r\n\r\n<p><img alt=\"\" src=\"/app/webroot/uploads/files/Tien-do/122016/1.jpg\" /><img alt=\"\" src=\"/app/webroot/uploads/files/Tien-do/122016/2.jpg\" /></p>\r\n',NULL,NULL,NULL,1),(15,138,NULL,NULL,NULL,'<p>T&iacute;nh đến ng&agrave;y 13/1/2017:</p>\r\n\r\n<p><strong>A/ Phần x&acirc;y th&ocirc;:</strong></p>\r\n\r\n<p>+ Ho&agrave;n th&agrave;nh 100% đổ b&ecirc; t&ocirc;ng dầm s&agrave;n tầng m&aacute;i</p>\r\n\r\n<p>+ C&ocirc;ng t&aacute;c x&acirc;y tường đang l&ecirc;n đến